mapper.update:
update corp set corp_intro=#{corpIntro}, corp_avrpay=#{corpAvrpay}, corp_kind=#{corpKind}, corp_pnumber=#{corpPnumber}, corp_headname=#{corpHeadname},corp_address1=#{corpAddress1}, COL_ADDRESS2=#{corpAddress2}, corp_industry=#{corpIndustry}, corp_startdate=#{corpStartdate}, corp_webaddress=#{corpWebaddress}, corp_tel=#{corpTel},corp_okcheck=0
,corp_imgurl=#{corpImgurl}
where corp_no=#{corpNo}

update.if:
update corpImg
set corpimg_url=#{corpimgUrl}
where corp_no=#{corpNo} and corpimg_sortno=#{corpimgSortno}
