mapper.select:
select r.*, c.CORP_NAME, c.CORP_IMGURL, l.LANGUAGE_NAME, w.WORKKIND_NAME, a.AREA_ADD1, a.AREA_ADD2 from RECRUITANNOUNCE r join CORP c on r.user_no=c.user_no join LANGUAGE l on r.LANGUAGE_NO=l.LANGUAGE_NO join WORKKIND w on r.WORKKIND_NO = w.WORKKIND_NO join AREALIST a on r.AREALIST_NO = a.AREA_NO where r.LANGUAGE_NO=#{languageNo}
and RECRUITANNOUNCE_ENDDATE>=sysdate
order by RECRUITANNOUNCE_ENDDATE

select r.*, c.CORP_NAME, c.CORP_IMGURL, l.LANGUAGE_NAME, w.WORKKIND_NAME, a.AREA_ADD1, a.AREA_ADD2 from RECRUITANNOUNCE r join CORP c on r.user_no=c.user_no join LANGUAGE l on r.LANGUAGE_NO=l.LANGUAGE_NO join WORKKIND w on r.WORKKIND_NO = w.WORKKIND_NO join AREALIST a on r.AREALIST_NO = a.AREA_NO where r.RECRUITANNOUNCE_WANTEDCARRER = #{indivCareer} AND r.LANGUAGE_NO=#{languageNo} AND a.AREA_ADD1=#{areaAdd1} AND a.AREA_ADD2=#{areaAdd2}
and RECRUITANNOUNCE_ENDDATE>=sysdate
order by RECRUITANNOUNCE_ENDDATE

select r.*, c.CORP_NAME, c.CORP_IMGURL, l.LANGUAGE_NAME, w.WORKKIND_NAME, a.AREA_ADD1, a.AREA_ADD2 from RECRUITANNOUNCE r join CORP c on r.user_no=c.user_no join LANGUAGE l on r.LANGUAGE_NO=l.LANGUAGE_NO join WORKKIND w on r.WORKKIND_NO = w.WORKKIND_NO join AREALIST a on r.AREALIST_NO = a.AREA_NO where r.RECRUITANNOUNCE_WANTEDCARRER = #{indivCareer} AND r.LANGUAGE_NO=#{languageNo} AND a.AREA_ADD1=#{areaAdd1}
and RECRUITANNOUNCE_ENDDATE>=sysdate
order by RECRUITANNOUNCE_ENDDATE
