mapper.select:
select m.*, (select user_id from users where USER_NO=m.GETUSER_NO) AS "USER_ID"
from MsgGetMsgView m
WHERE MESSAGE_NO=#{messageNo}

mapper.update:
update message
set SEND_DELFLAG=1
where MESSAGE_NO=#{messageNo}

update getMessage
set GETMESSAGE_DELFLAG=1
where MESSAGE_NO=#{messageNo}
