mapper.select:
		select *
		from
		(
		    select A.*, rownum as RNUM
		    from
		    (
		        		select * from POSITIONSUGGEST
                        where user_no=#{userNo} and send_delflag=2
                        order by POSITIONSUGGEST_NO desc
		    )A
		)

		where RNUM > #{firstRecordIndex} 
		and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}
