mapper.select:
select *
	from
	(
	    select A.*, rownum as RNUM
	    from
	    (
	        select * from CORPREVIEW
	        where corp_no=#{corpNo}
	    )A
	)

	where RNUM > #{firstRecordIndex} and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update CORPREVIEW
set CORPREVIEW_DELETECHECK=1
where CORPREVIEW_NO=#{corpreviewNo}
